(function() {
  var formsShowCtrl;

  formsShowCtrl = function() {
    return function($scope, Form, $routeParams, Response, $location) {
      var checkForm, checkIsFilled;
      $scope.ready = false;
      $scope.formIsFilled = false;
      Form.show({
        id: $routeParams.id
      }, function(response) {
        $scope.form = response.form;
        return $scope.ready = true;
      });
      checkIsFilled = function() {
        var i, len, question, ready, ref;
        ready = true;
        ref = $scope.form.questions;
        for (i = 0, len = ref.length; i < len; i++) {
          question = ref[i];
          if (!question.response) {
            ready = false;
          }
        }
        $scope.formIsFilled = ready;
        return $scope.$apply();
      };
      checkForm = _.debounce(checkIsFilled, 200);
      $scope.checkForm = function() {
        return checkForm();
      };
      return $scope.createResponse = function() {
        var data;
        data = _.map($scope.form.questions, function(q) {
          return [q.id, q.response];
        });
        return Response.create({
          response: {
            form_id: $scope.form.id,
            data: data
          }
        }, function(response) {
          return $location.path("/forms/" + $scope.form.id + "/responses");
        });
      };
    };
  };

  angular.module("app.forms").controller("formsShowCtrl", ["$scope", "Form", "$routeParams", "Response", "$location", formsShowCtrl()]);

}).call(this);
(function() {
  var formsResponsesShowCtrl;

  formsResponsesShowCtrl = function() {
    return function($scope, FormResponse, $routeParams) {
      var isChartable, prepareQuestion, prepareResponse;
      $scope.ready = false;
      isChartable = function(type) {
        return type === "checkbox" || type === "radio" || type === "text";
      };
      prepareQuestion = function(question) {
        return {
          id: question.id,
          title: question.title,
          type: question.question_type,
          data: []
        };
      };
      prepareResponse = function(response) {
        var questions;
        questions = {};
        _.map(response.data.form.questions, function(question) {
          return questions[question.id] = prepareQuestion(question);
        });
        _.map(response.data.responses, function(response) {
          return _.map(response.data, function(data) {
            if (questions[data[0]].type === "text") {
              return questions[data[0]].data.push(response.created_at);
            } else {
              return questions[data[0]].data.push(data[1]);
            }
          });
        });
        _.map(questions, function(q) {
          if (isChartable(q.type)) {
            q.data = _.countBy(q.data);
          }
          if (q.type === "text") {
            return q.data[I18n.t("js.helpers.created_at")] = 0;
          }
        });
        return questions;
      };
      return FormResponse.index({
        form_id: $routeParams.form_id
      }, function(response) {
        $scope.responsesLength = response.data.responses.length ? true : false;
        $scope.questions = prepareResponse(response);
        return $scope.ready = true;
      });
    };
  };

  angular.module("app.forms").controller("formsResponsesShowCtrl", ["$scope", "FormResponse", "$routeParams", formsResponsesShowCtrl()]);

}).call(this);
(function() {
  angular.module("app.responses", []);

}).call(this);
(function() {
  var responsesIndexCtrl;

  responsesIndexCtrl = function() {
    return function($scope, Response, $routeParams) {
      $scope.ready = false;
      return Response.index("", function(response) {
        $scope.responses = response.responses;
        return $scope.ready = true;
      });
    };
  };

  angular.module("app.responses").controller("responsesIndexCtrl", ["$scope", "Response", "$routeParams", responsesIndexCtrl()]);

}).call(this);
(function() {
  var responsesShowCtrl;

  responsesShowCtrl = function() {
    return function($scope, Response, $routeParams) {
      $scope.ready = false;
      $scope.isArray = function(object) {
        return typeof object === "object";
      };
      return Response.show({
        id: $routeParams.id
      }, function(response) {
        var dataResponse;
        $scope.data = {};
        $scope.data.form = response.data.form;
        dataResponse = {};
        _.map(response.data.response.data, function(item) {
          return dataResponse[item[0]] = item[1];
        });
        $scope.data.response = dataResponse;
        return $scope.ready = true;
      });
    };
  };

  angular.module("app.responses").controller("responsesShowCtrl", ["$scope", "Response", "$routeParams", responsesShowCtrl()]);

}).call(this);
(function() {
  angular.module("app", ["ngRoute", "templates", "highcharts-ng", "Devise", "ngCookies", "ngResource", "ngSanitize", "ui.slider", "checklist-model", "pascalprecht.translate", "mgcrea.ngStrap.core", "mgcrea.ngStrap.modal", "mgcrea.ngStrap.helpers.dimensions", "app.core", "app.shared", "app.auth", "app.dashboard", "app.forms", "app.responses"]);

  angular.module("app").config([
    "$translateProvider", function($translateProvider) {
      $translateProvider.preferredLanguage(I18n.locale);
      $translateProvider.useSanitizeValueStrategy("sanitize");
      return $translateProvider.translations(I18n.locale, I18n.translations[I18n.locale]);
    }
  ]);

}).call(this);































